/**
 * Parser error model.
 * @param {string} type code  
 * @param {object} start token range 
 * @param {object} end token range 
 */
function ParserError(type) {
    this.type = type;
    this.message = "";
    this.options = {};
    this.range = {
        first_line: 0,
        first_column: 0,
        last_line: 0,
        last_column: 0
    };
}

module.exports.ParserError = ParserError;