/**
 * Recoverable parser error. Error which does not halt parser.
 * @param {string} type code  
 * @param {object} start token range 
 * @param {object} end token range 
 */
function RecoverableParserError(type, startTokenRange, endTokenRange) {
    this.type = type;
    this.startTokenRange = startTokenRange;
    this.endTokenRange = endTokenRange;
}

module.exports.RecoverableParserError = RecoverableParserError;