import * as React from "react";
import { observer } from "mobx-react";

export interface JsonFormattterProps {
    objectToJson: any;
    multiLine?: boolean;
}

export class JsonFormatter extends React.Component<JsonFormattterProps, any> {
    render() {
        var multiLine = this.props.multiLine;
        var jsonText = JSON.stringify(this.props.objectToJson, null, 4);
        if (multiLine != null && multiLine.valueOf()) {
            return <pre>{jsonText}</pre>;
        }
        else {
            return <span>{jsonText}</span>;
        }
    }
}