import { BaseSparqlEditor } from "./BaseSparqlEditor";
import { IPluginManagerFactory } from '../factory/plugin/PluginManagerFactory';
import { ReleasePluginManagerFactory } from "../factory/plugin/ReleasePluginManagerFactory";
import { SparqlEditorSettings } from "EditorComponent/src/editor/SparqlEditorSettings";
import { ComponentElement } from "EditorComponent/node_modules/@types/react";

/**
 * Release version of sparql editor.
 */
export class SparqlEditor extends BaseSparqlEditor {

    constructor(public container: HTMLElement,
        sparqlEditorSettings: SparqlEditorSettings) {
        super(container, sparqlEditorSettings, new ReleasePluginManagerFactory(sparqlEditorSettings))
    }

    protected decorateRenderElement(editorAppElement: ComponentElement<any, any>): ComponentElement<any, any> {
        return editorAppElement;
    }
}