import { PluginManager } from "../../plugins/PluginManager";
import { BasePluginManagerFactory } from "./PluginManagerFactory";
import { ParserLoggerTool } from "../../plugins/Tools/ParserLoggerTool/ParserLoggerTool";
import { ParserVisualiserTool } from "../../plugins/Tools/ParserVisualiserTool/ParserVisualiserTool";
import { CursorInjectionPlugin } from "../../plugins/CursorInjectionPlugin/CursorInjectionPlugin";
import { SparqlEditorSettings } from "EditorComponent/src/editor/SparqlEditorSettings";

export class DebugPluginManagerFactory extends BasePluginManagerFactory {

    constructor(sparqlEditorSettings: SparqlEditorSettings) {
        super(sparqlEditorSettings);
    }

    registerAdditionalPlugins(pluginMangager: PluginManager): void {
        pluginMangager.registerPlugin(new ParserVisualiserTool());
        pluginMangager.registerPlugin(new ParserLoggerTool());
        pluginMangager.registerPlugin(new CursorInjectionPlugin());
    }
}