import { IPlugin } from '../IPlugin';
import { SparqlCompletionItemProvider } from './SparqlCompletionItemProvider';

export class AutocompletitionPlugin implements IPlugin {
    init(monacoEditor: monaco.editor.IStandaloneCodeEditor): void {
        monaco.languages.registerCompletionItemProvider('sparql', new SparqlCompletionItemProvider());
    }
}