import { ErrorCheckPlugin } from './ErrorCheckPlugin/ErrorCheckPlugin';
import { PluginManager } from './PluginManager';
import { AutocompletitionPlugin } from './AutocompletitionPlugin/AutocompletitionPlugin';
import { EditorChangePlugin } from "./EditorChangePlugin/EditorChangePlugin";
import { SyntaxHighlightPlugin } from "./SyntaxHighlightPlugin/SyntaxHighlightPlugin";

export class PluginManagerFactory {
    createPluginManager() {
        var pluginMangager = new PluginManager();
        //inject all plugins
        pluginMangager.registerPlugin(new EditorChangePlugin());
        pluginMangager.registerPlugin(new SyntaxHighlightPlugin());
        pluginMangager.registerPlugin(new ErrorCheckPlugin());
        pluginMangager.registerPlugin(new AutocompletitionPlugin());

        return pluginMangager;
    }
}