import { IEditorPluginApi } from '../EditorPluginApi';
import { IPlugin } from '../IPlugin';
import { CompletionItemProvider } from "./Providers/CompletionItemProvider";
import { HoverProvider } from "./Providers/HoverProvider";
import { SignatureHelpProvider } from "./Providers/SignatureHelpProvider";

import * as  _ from "lodash";

export class SparqlLanguagePlugin implements IPlugin {
    init(editorPluginApi: IEditorPluginApi): void {
        var sparqlLanguageServer = editorPluginApi.getSparqlLanguageServer();
        monaco.languages.registerCompletionItemProvider('sparql', new CompletionItemProvider(sparqlLanguageServer));
        monaco.languages.registerSignatureHelpProvider('sparql', new SignatureHelpProvider(sparqlLanguageServer));
        monaco.languages.registerHoverProvider('sparql', new HoverProvider(sparqlLanguageServer));
    }
}