import { EditorSettingsStore } from '../../../../store/EditorSettingsStore';
import * as React from "react";
import { observer } from "mobx-react";
import { JsonFormatter } from "../../../../components/utils/JsonFormatter"
import { List, ListItem, Divider } from "material-ui"

export interface ParserLoggerStackViewProps {
    stack: any[];
}

export class ParserLoggerStackView extends React.Component<ParserLoggerStackViewProps, any> {
    render() {
        return <div>
            Stack:
            <List>
                {this.props.stack.map((stackItem: any, i: number) => (
                    [<ListItem key={i} className="collection-item" >
                        <JsonFormatter objectToJson={stackItem} />
                    </ListItem>, <Divider />]))
                }
            </List>
        </div>
    }
}
