import * as React from "react";
import { IEditorPluginApi } from '../../EditorPluginApi';
import { IPlugin } from '../../IPlugin';
import { Tool, ToolType } from "../../../store/ToolStore";
import { ParserLogger } from "../../../components/ParserLogger";
import { ParserVisualiser } from "EditorComponent/src/components/ParserVisualiser";

export class ParserVisualiserTool implements IPlugin {
    init(editorPluginApi: IEditorPluginApi): void {
        editorPluginApi.getToolStore().tools.push(
            new Tool(
                <ParserVisualiser ediorSettingsStore={editorPluginApi.getEditorSettings()} />,
                ToolType.Bottom
            ));
    }
}