import * as React from "react";
import { IEditorSettings } from "EditorComponent/src/react/model/EditorSettings";

export interface EditorSettingsComponentProps {
    editorSettings: IEditorSettings;
    toggleSettings(name: string): void
}

export class EditorSettingsComponent extends React.Component<EditorSettingsComponentProps, any> {
    constructor() {
        super();
    }

    render() {
        var editorSettings = this.props.editorSettings;
        var editorSettingsList = editorSettings.itemsDefinitions
            .map(x => <div key={x.name} onClick={() => this.props.toggleSettings(x.name)}>Name {x.name} title {x.title} value {editorSettings.items.get(x.name).toString()} </div>);

        return (
            <div className="row">
                {editorSettingsList}
            </div>);
    }
}