import * as React from "react";

export interface ListItem {
    id: number;
    name: string;
}

export interface ListComponentProps {
    itemList: ListItem[]
    settings1: boolean
}

export class ListComponent extends React.Component<ListComponentProps, any> {
    constructor() {
        super();
    }

    render() {
        var listValue = this.props.itemList
            .map(x => <li key={x.id}>{x.id} {x.name} {this.props.settings1.toString()}</li>)

        return (<ul>
            {listValue}
        </ul>);
    }
}