import {EditorSettingsStore} from '../store/EditorSettingsStore';

import * as React from "react";
import { observer } from "EditorComponent/node_modules/mobx-react";

export interface EditorSettingsComponentProps {
    editorSettingsStore: EditorSettingsStore;
}

@observer
export class EditorSettingsComponent extends React.Component<EditorSettingsComponentProps, any> {
    constructor() {
        super();
    }

    toggleSettings(name: string) {
        var vaule = this.props.editorSettingsStore.getSettings(name) != true;
        console.log('toggle' + name + vaule.toString());
        this.props.editorSettingsStore.setSettings(name, vaule);
    }

    render() {
        console.log(this.props.editorSettingsStore.items.toString());
        var editorSettingsList = this.props.editorSettingsStore.itemsDefinitions
            .map(x => <div key={x.name} onClick={() => this.toggleSettings(x.name)}>Name {x.name} title {x.title} value {this.props.editorSettingsStore.getSettings(x.name).toString()} </div>);

        return (
            <div className="row">
                {editorSettingsList}
            </div>);
    }
}