/// <reference path="../node_modules/monaco-editor/monaco.d.ts" />
import * as angular from 'angular';
import { Pokus } from "EditorComponent/src/pokus";

// Define the `testServerApp` module
var testServerApp = angular
    .module('sparqlTestApp', []);

// Define the `PhoneListController` controller on the `testServerApp` module
testServerApp
    .controller('SparqlTestController', ['$scope', function SparqlTestController($scope) {
        $scope.testValue = "asdf";

        $scope.runNewParserClick = function () {
            $scope.testValue = "aaaa";
            console.log('sss');
            var pokus = new Pokus();
            pokus.writeSome();
        };
    }]);

(<any>window).requirejs.config({ paths: { vs: '../node_modules/monaco-editor/min/vs' } });
(<any>window).requirejs(['vs/editor/editor.main'], function () {
    var editor = monaco.editor.create(document.getElementById('container'), {
        value: [
            'function x() {',
            '\tconsole.log("Hello world!");',
            '}'
        ].join('\n'),
        language: 'javascript'
    });
});