import { SparqlLanguageServer } from "SparqlAutocompletion";

export function getSparqlLanguageServer() {

    if ((<any>document).sparqlLanguageServer == null) {
        var ontologyContent1 = require('raw-loader!../resources/dbpedia.ttl');
        var ontologyContent2 = require('raw-loader!../resources/schemaorg.owl.ttl');
        (<any>document).sparqlLanguageServer = new SparqlLanguageServer([ontologyContent1, ontologyContent2]);
    }
    return (<any>document).sparqlLanguageServer;
}