import { SparqlLanguageServer } from "SparqlAutocompletion";

export function getSparqlLanguageServer() {
    
    if ((<any>document).sparqlLanguageServer == null) {
        var ontologyContent = require('raw-loader!../resources/dbpedia.ttl');
        (<any>document).sparqlLanguageServer = new SparqlLanguageServer(ontologyContent);
    }
    return (<any>document).sparqlLanguageServer;
}