import { SparqlLanguageServer } from "SparqlAutocompletion";

export class SparqlLanguageServerProvider {
    private sparqlLanguageServer: SparqlLanguageServer = null;

    constructor() {
        // empty
    }

    getSparqlLanguageServer() {
        return this.sparqlLanguageServer;
    }

    setSparqlLanguageServer(sparqlLanguageServer: SparqlLanguageServer) {
        this.sparqlLanguageServer = sparqlLanguageServer;
    }
}