import { CompletionItemProvider } from "./Providers/CompletionItemProvider";
import { HoverProvider } from "./Providers/HoverProvider";
import { SignatureHelpProvider } from "./Providers/SignatureHelpProvider";
import { SparqlLanguageServer } from "SparqlAutocompletion";
import { SparqlLanguageServerProvider } from "./SparqlLanguageServerProvider";
import * as  _ from "lodash";

export function registerSparqlProviders(sparqlLanguageServerProvider: SparqlLanguageServerProvider) {
    monaco.languages.registerCompletionItemProvider("sparql", new CompletionItemProvider(sparqlLanguageServerProvider));
    monaco.languages.registerSignatureHelpProvider("sparql", new SignatureHelpProvider(sparqlLanguageServerProvider));
    monaco.languages.registerHoverProvider("sparql", new HoverProvider(sparqlLanguageServerProvider));
}
