import { observable } from "mobx";

export enum ToolType {
    SideBar,
    Debug
}

export class Tool {
    constructor(public content: JSX.Element, public toolName: string, public type = ToolType.SideBar) {

    }
}

export class ToolStore {
    @observable tools: Tool[] = [];

    public flush() {
        this.tools = [];
    }
}