export function createHttpRequest(url: string, resolve: (responseText: string) => void, reject: () => void) {
    var xhr = new XMLHttpRequest();
    xhr.open('GET', url);
    xhr.send(null);
    xhr.onreadystatechange = function () {
        if (xhr.readyState === 4) {
            if (xhr.status === 200) {
                resolve(xhr.responseText);
            }
            else {
                reject();
            }
        }
    };
}
