import * as path from "path";
import * as fs from "fs";
import { CommanderWrapperError } from "EditorTool/src/Commander/CommanderWrapper";

export function readFileFromArgument(fileArgument: string): string {
    try {
        var filePath = path.join(process.cwd(), fileArgument);
        var fileContent = fs.readFileSync(filePath).toString();
        return fileContent;
    } catch (ex) {
        throw new CommanderWrapperError(`Could not read file '${fileArgument}'.`);
    }
}

export function writeIntoFileArgument(fileArgument: string, fileContent: string) {
    try {
        var filePath = path.join(process.cwd(), fileArgument);
        fs.writeFileSync(filePath, fileContent);
    } catch (ex) {
        throw new CommanderWrapperError(`Could write into file '${fileArgument}'.`);
    }
}