import * as http from "http";

export function createStaticServerOnPort(port: number, content: string) {
    var server = http.createServer((req, res) => {
        res.writeHead(200, {
            "Content-Type": "text/plain",
            "Access-Control-Allow-Origin": "*"
        });
        res.end(content);
    });

    server.listen(port);
}