import { IOntologyClass } from '../../Ontology/OntologyClass';
import { OntologyAssertions } from '../../Ontology/OntologyAssertions';
import { BasePropertySelector } from "./BasePropertySelector";
import * as _ from "lodash";

export class FullPropertySelector extends BasePropertySelector {
    constructor(ontologyAssertions: OntologyAssertions) {
        super(ontologyAssertions);
    }

    getAcessibleClasses(ontologyClass: IOntologyClass) {
        return this.ontologyAssertions
            .findSuperSetClasses(ontologyClass);
    }
}