import { IPropertySelector } from "./IPropertySelector";
import { FullPropertySelector } from "SparqlAutocompletion/src/Autocompletition/PropertySelector/FullPropertySelector";
import { RestrictedPropertySelector } from "SparqlAutocompletion/src/Autocompletition/PropertySelector/RestrictedPropertySelector";
import { OntologyAssertions } from "../../Ontology/OntologyAssertions";

export class PropertySelectorFactory {
    createPropertySelector(propertySelectorType: "Restricted" | "Full", ontologyAssertions: OntologyAssertions): IPropertySelector {
        switch (propertySelectorType) {
            case "Full":
                return new FullPropertySelector(ontologyAssertions);
            case "Restricted":
                return new RestrictedPropertySelector(ontologyAssertions)
            default:
                throw new Error("Invalid property selector type inserted.");
        }
    }
}