export interface ISparqlLanguageServerSettings {
    propertySelectorType?: "Restricted" | "Full";
}

export class SparqlLanguageServerSettings {

    public constructor(private sparqlLanguageServerSettings: ISparqlLanguageServerSettings) {

    }

    getPropertySelectorType(): "Restricted" | "Full" {
        var propertySelectorType = this.sparqlLanguageServerSettings.propertySelectorType
        if (propertySelectorType !== "Restricted" && propertySelectorType !== "Full") {
            return "Restricted";
        }
        return propertySelectorType;
    }
}