export interface IAlgorithmError {
    getMessage(): string;
}

export class AlgorithmErrorLogger {

    private _errors: IAlgorithmError[] = [];

    public logError(algorithmError: IAlgorithmError) {
        this._errors.push(algorithmError);
    }

    public get Errors() {
        return this._errors;
    }

    /**
     * Method for debug purpose
     */
    public writeErrorToConsole() {
        this._errors.forEach(x => console.log(x.getMessage()));
    }
}