import { StringDictionary } from "../Collections/Dictionary";

export class OntologyLabel {
    private _labels: StringDictionary<string> = {};

    get labels(): Readonly<StringDictionary<string>> {
        return this._labels;
    }

    getLabelByLanguage(labelLanguage: string) {
        return this._labels[labelLanguage];
    }

    addLanguageLabel(labelContent: string, labelLanguage: string) {
        this._labels[labelLanguage] = labelContent;
    }

    addLabel(label: string) {
        // Regex are statefull => can not be extracted from class 
        var ontologyLabelRegex = /\"([^\"]*)\"@([a-zA-Z0-9]+)/g;
        var parsedLabel = ontologyLabelRegex.exec(label);
        if (parsedLabel == null) {
            return;
        }
        this.addLanguageLabel(parsedLabel[1], parsedLabel[2]);
    }

    getLabelCount() {
        return Object.keys(this._labels).length;
    }

    containsAnyLabel() {
        return this.getLabelCount() > 0;
    }
}