export class OntologyLabel {
    private labels: { [index: string]: string } = {};

    getLabelByLanguage(labelLanguage: string) {
        return this.labels[labelLanguage];
    }

    addLanguageLabel(labelContent: string, labelLanguage: string) {
        this.labels[labelLanguage] = labelContent;
    }

    addLabel(label: string) {
        // Regex are statefull => can not be extracted from class 
        var ontologyLabelRegex = /\"([^\"]*)\"@([a-zA-Z0-9]+)/g;
        var parsedLabel = ontologyLabelRegex.exec(label);
        if (parsedLabel == null) {
            return;
        }
        this.addLanguageLabel(parsedLabel[1], parsedLabel[2]);
    }

    getLabelCount() {
        return Object.keys(this.labels).length;
    }
}