import { OntologyLabel } from "../../Ontology/OntologyLabel";
import { BaseTypeSerializer, SerializationContext } from "../TypeSerializer";
import { StringDictionary } from "../../Collections/Dictionary";

export class OntologyLabelSerializer extends BaseTypeSerializer<OntologyLabel> {

    serialize(serializeObject: OntologyLabel, serializationContext: SerializationContext) {
        return serializeObject.labels;
    }

    deserialize(payload: any, serializationContext: SerializationContext): OntologyLabel {
        var ontologyLabel = new OntologyLabel();
        for (var languageKey in payload) {
            ontologyLabel.addLanguageLabel(payload[languageKey], languageKey);
        }
        return ontologyLabel;
    }
}