import { OntologyConcept } from "../../../src/Ontology/OntologyHierarchy";
import { AtomicClass } from "../../../src/Ontology/OntologyClass";

/**
 * Creates node atomic class.
 */
export function nc(index: number) {
    return new AtomicClass(`http://www.example.com/Node${index}`);
}

/**
 * Creates ontology concept with given atomic class.
 * */
export function no(indexes: number[]) {
    return new OntologyConcept(indexes.map(index => nc(index)));
}