import * as chai from "chai";
import { SparqlLanguageServerSettings } from "SparqlAutocompletion/src/Autocompletition/SparqlLanguageServerSettings";

describe("SparqlLanguageServerSettings", () => {
    it("should get specified language tag.", () => {
        var settings = { languageTag: "cz" };
        var sparqlLanguageServerSettings = new SparqlLanguageServerSettings(settings);
        var languageTag = sparqlLanguageServerSettings.getLanguageTag();
        chai.expect(languageTag).equal("cz");
    });

    it("should get default language tag.", () => {
        var sparqlLanguageServerSettings = new SparqlLanguageServerSettings({});
        var languageTag = sparqlLanguageServerSettings.getLanguageTag();
        chai.expect(languageTag).equal("en");
    });
});