import { getGraphTopologicalOrdering, TopologicalOrderingGraph } from '../../../src/Ontology/Algorithms/TopologicalOrdering';
import * as chai from 'chai';


describe('Topological ordering', () => {
    it('shoud resolve complex graph.', () => {
        var graph = new TopologicalOrderingGraph();
        graph.allocateNodes(7);
        graph.addEdge(0, 1);
        graph.addEdge(0, 2);
        graph.addEdge(0, 4);
        graph.addEdge(1, 3);
        graph.addEdge(2, 3);
        graph.addEdge(2, 5);
        graph.addEdge(3, 4);
        graph.addEdge(3, 5);
        graph.addEdge(5, 6);

        var topologicalOrdering = getGraphTopologicalOrdering(graph);
        chai.expect(topologicalOrdering).to.deep.equal([0, 1, 2, 3, 4, 5, 6]);
    });
});