import { OntologyLabel } from "../../src/Ontology/OntologyLabel";
import * as chai from "chai";

function testLabelSuccess(labelContent: string, language: string) {
    var ontologyLabel = new OntologyLabel();
    ontologyLabel.addLabel(`"${labelContent}"@${language}`);
    var label = ontologyLabel.getLabelByLanguage(language);
    chai.expect(label).to.equals(labelContent);
}

describe("OntologyLabel", () => {
    it("should parse label strings.", () => {
        testLabelSuccess("label", "en");
        testLabelSuccess("label \r\n label", "j");
        testLabelSuccess("labelasdfaASAS ass dfd++9ěš", "strangeLanguage5");
        testLabelSuccess("", "cs");
    });

    it("should not parse wrong label strings.", () => {
        var ontologyLabel = new OntologyLabel();
        ontologyLabel.addLabel("\"label\"@");
        ontologyLabel.addLabel("label");
        ontologyLabel.addLabel("label@en");
        ontologyLabel.addLabel("\"label@en");
        ontologyLabel.addLabel("label\"@en");
        
        var label = ontologyLabel.getLabelByLanguage("");
        chai.expect(label).to.equals(undefined);
        chai.expect(ontologyLabel.getLabelCount()).to.equals(0);
    });
});