import { Serializer } from '../../../src/Serialization/Serializer';
import { OntologyLabel } from '../../../src/Ontology/OntologyLabel';
import * as chai from 'chai';

var typeSerializerName = "OntologyLabel";

describe('OntologyLabelSerializer', () => {
    it('should serialize and deserialize empty label.', () => {
        var ontologyLabel = new OntologyLabel();
        var serializer = new Serializer();
        var serialized = serializer.serialize(typeSerializerName, ontologyLabel);
        var deserialized = <OntologyLabel>serializer.deserialize(typeSerializerName, serialized);
        chai.expect(deserialized).to.deep.equal(ontologyLabel);
    });

    it('should serialize and deserialize label.', () => {
        var ontologyLabel = new OntologyLabel();
        ontologyLabel.addLanguageLabel("Person", "en");
        ontologyLabel.addLanguageLabel("Člověk", "cs");
        var serializer = new Serializer();
        var serialized = serializer.serialize(typeSerializerName, ontologyLabel);
        var deserialized = <OntologyLabel>serializer.deserialize(typeSerializerName, serialized);
        chai.expect(deserialized).to.deep.equal(ontologyLabel);
    });
});