import { OntologyHierarchy } from '../../src/Ontology/OntologyHierarchy';
import { OntologyAssertions } from '../../src/Ontology/OntologyAssertions';
import { OntologyHierarchyBuilder } from '../../src/Ontology/Algorithms/OntologyHierarchyBuilder';
import { OntologyAssertionsBuilder } from '../../src/Ontology/Algorithms/OntologyAssertionBuilder';
import { TurtleGraphWrapper } from '../../src/GraphTools/TurtleGraphWrapper';

export function createOntologyAssertionsFromTurtle(ontologyContent: string) {
    var graph = new TurtleGraphWrapper(ontologyContent);
    var ontologyAssertionsResult = (new OntologyAssertionsBuilder()).buildOntologyAsertions(graph);
    return ontologyAssertionsResult.ontologyAssertions;
}

export function createOntologyHierarchyFromTurtle(ontologyContent: string): OntologyHierarchy {
    var ontologyAssertions = createOntologyAssertionsFromTurtle(ontologyContent);
    var ontologyHierarchyBuilder = new OntologyHierarchyBuilder();
    var ontologyHiarchy = ontologyHierarchyBuilder.createOntologyHierarchy(ontologyAssertions);
    return ontologyHiarchy;
}