import { buildOntologyAssertions } from "../../src/Ontology/Algorithms/OntologyAssertionBuilder";
import { TurtleGraphWrapper } from "../../src/GraphTools/TurtleGraphWrapper";
import { OntologyAssertions } from "../../src/Ontology/OntologyAssertions";

export function createOntologyAssertionsFromTurtle(ontologyContent: string): OntologyAssertions {
    var graph = new TurtleGraphWrapper(ontologyContent);
    var ontologyAssertionsResult = buildOntologyAssertions(graph);
    return ontologyAssertionsResult.ontologyAssertions;
}